function Category(){}Category.prototype.run=function(){var t=this;t.listenAddCategory(),t.listendelCategory(),t.listeneditCategory()},Category.prototype.listenAddCategory=function(){$("#add-btn").click(function(){blogalert.alertOneInput({text:"分类名称",confirmCallback:function(t){blogajax.post({url:"/cms/add_category/",data:{name:t},success:function(t){200===t.code&&window.location.reload()}})}})})},Category.prototype.listendelCategory=function(){$(".delete-btn").click(function(){var t=$(this).parent().parent().attr("data-pk");blogalert.alertConfirm({text:"确定要删除吗?",confirmCallback:function(){blogajax.post({url:"/cms/del_category/",data:{categoryId:t},success:function(t){200===t.code&&window.location.reload()}})}})})},Category.prototype.listeneditCategory=function(){$(".edit-btn").click(function(){var t=$(this).parent().parent(),a=t.attr("data-pk"),o=t.attr("data-name");blogalert.alertOneInput({text:"编辑分类名称",value:o,placeholder:"请输入分类名称",confirmCallback:function(t){blogajax.post({url:"/cms/edit_category/",data:{name:t,categoryId:a},success:function(t){200===t.code&&window.location.reload()}})}})})},$(function(){(new Category).run()});